import axios from "axios";

const BASE_URL= "http://localhost:5000/api";
const TOKEN = "eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJpZCI6IjYyNmFiMWMxZWU3N2ZjZTQxMzJkZGVkYyIsImlzQWRtaW4iOnRydWUsImlhdCI6MTY1MjczMTEwNCwiZXhwIjoxNjUyOTkwMzA0fQ.JQn3jkaCvkhsNO1dHq_1K4Gc56PHa6zMzeVO8G4lgP4"

export const publicRequest = axios.create({
    baseURL: BASE_URL,
});

export const userRequest = axios.create({
    baseURL: BASE_URL,
    header:{token:`Bearer ${TOKEN}`}
});
