import axios from "axios";

const BASE_URL= "http://localhost:5000/api";
const TOKEN = "eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJpZCI6IjYyNzE0NjY3MjYxOGUxZDA4MTBkMTgzYSIsImlzQWRtaW4iOnRydWUsImlhdCI6MTY1MjQ2MTkzOCwiZXhwIjoxNjUyNzIxMTM4fQ.zG4OdwmNuRMbPrsQloiWllYoTojygqhjSw6YA_8ke-w"

export const publicRequest = axios.create({
    baseURL: BASE_URL,
});

export const userRequest = axios.create({
    baseURL: BASE_URL,
    header:{token:`Bearer ${TOKEN}`}
});
