import styled from "styled-components"
import { mobile, mobileM } from "../responsive";


const Container = styled.div`
    width: 100vw;
    height: 100vh;
    background: linear-gradient(rgba(255, 255, 255, 0.5), rgba(255, 255, 255, 0.5)), url("https://images.pexels.com/photos/6984650/pexels-photo-6984650.jpeg?auto=compress&cs=tinysrgb&dpr=2&h=650&w=940") center;
    background-size: cover; 
    display: flex;
    align-items: center;
    justify-content: center;
    ${mobile({ backgroundSize: "120vh" })};
    ${mobileM({ backgroundSize: "120vh" })};


`;

const Wrapper = styled.div`
    width: 30vw;
    padding: 20px;
    background-color: white;
    ${mobile({ width: "75%" })};
    ${mobileM ({ width: "75%" })};

    
`;
const Title = styled.h1`
    font-size: 24px;
    font-weight: 300;
  
`;
const Form = styled.form`
    display: flex;
    flex-direction: column;
`;
const Input = styled.input`
    flex: 1;
    min-width: 40%;
    margin: 10px 0;
    padding: 10px;
`;
const Agreement = styled.span`
    font-size: 12px;
    margin: 20px 0;

`;
const Button = styled.button`
    width: 30vw;
    border: none;
    padding: 15px 20px;
    background-color: teal;
    cursor: pointer;
    margin-bottom: 10px;
`;

const Link = styled.a`
    margin: 5px 0;
    font-size: 12px;
    text-decoration: underline;
    cursor: pointer;

`;

const Login = () => {
    return (
        <Container>
            <Wrapper>
                <Title>SIGN IN</Title>
                <Form>
                    <Input placeholder="username" />
                    <Input placeholder="password" />
                    <Button>LOG IN</Button>
                    <Link>DO NOT YOU REMEMBER THE PASSWORD?</Link>
                    <Link>CREATE A NEW ACCOUNT</Link>
                </Form>
            </Wrapper>
        </Container>
    )
}

export default Login
