import styled from "styled-components";
import Navbar from "../component/Navbar";
import Announcement from "../component/Announcement";
import Newsletter from "../component/Newsletter";
import Footer from "../component/Footer";

const Container = styled.div`
    
`;

const Wrapper = styled.div`
    padding: 50px;
    display: flex;
`;
const ImgContainer = styled.div`
    flex: 1;
`;
const Image = styled.img`
    width: 100%;
    height: 90vh;
    object-fit: cover;
`;
const InfoContainer = styled.div`
    flex: 1;
    padding: 0px 50px;
`;
const Title = styled.h1`
    font-weight: 200;
`;
const Desc = styled.p`
    margin: 20px 0px;

`;
const Price = styled.span`
    font-weight: 100;
    font-size: 40px;

`;

const FilterContainer = styled.div`
    width: 50%;
    display: flex;
    justify-content: space-between;

`;
const Filter = styled.div`
    display: flex;
    align-items: center;   
`;
const FilterTitle = styled.span`
    font-size: 20px;
    font-weight: 200;
`;
const FilterColor = styled.div`
    width: 20px;
    height: 20px;
    border-radius: 50%;
    background-color: ${props=>props.color};
    margin: 0px 5px;

`;
const FilterSize = styled.select``;
const FilterSizeOption = styled.option``;






const Product = () => {
    return (
        <Container>
            <Announcement />
            <Navbar />
            <Wrapper>
                <ImgContainer>
                    <Image src="https://i.ibb.co/S6qMxwr/jean.png" />
                </ImgContainer>
                <InfoContainer>
                    <Title>
                        Denim Jumpsuit
                    </Title>
                    <Desc>
                        Lorem ipsum dolor sit amet consectetur adipisicing elit. Veritatis, voluptatibus. Nam tempore quasi libero eos aperiam dicta nobis, ex ad repudiandae odit assumenda quibusdam repellat. Perspiciatis dolor amet laudantium fugiat!
                    </Desc>
                    <Price>$20.00</Price>
                    <FilterContainer>
                        <Filter>
                            <FilterTitle>Color:</FilterTitle>
                            <FilterColor color="black"></FilterColor>
                            <FilterColor color="darkblue"></FilterColor>
                            <FilterColor color="gray"></FilterColor>
                        </Filter>
                        <Filter>
                            <FilterTitle>Size</FilterTitle>
                            <FilterSize>
                                <FilterSizeOption>XS</FilterSizeOption>
                                <FilterSizeOption>S</FilterSizeOption>
                                <FilterSizeOption>M</FilterSizeOption>
                                <FilterSizeOption>L</FilterSizeOption>
                                <FilterSizeOption>XL</FilterSizeOption>
                            </FilterSize>
                        </Filter>
                    </FilterContainer>
                </InfoContainer>


            </Wrapper>
            <Newsletter />
            <Footer />
        </Container>
    )
}

export default Product
