import { Facebook, Instagram, Mail, Phone, Pinterest, Room, Twitter } from '@mui/icons-material';
import React from 'react'
import styled from 'styled-components';
import { mobile, mobileM } from '../responsive';

const Container = styled.div`
    display: flex;
    ${mobile({ flexDirection: "column" })};
    ${mobileM({ flexDirection: "column" })};


`;

const Left = styled.div`
    flex: 1;
    display: flex;
    flex-direction: column;
    padding: 20px;
`;

const Logo = styled.h1``;

const Desc = styled.p`
    margin: 20px 0px;
`;

const SocialContainer = styled.div`
    display: flex;
`;

const SocialIcon = styled.div`
    width: 40px;
    height:40px;
    border-radius: 50px;
    color: white;
    background-color: #${props => props.color};
    display: flex;
    align-items: center;
    justify-content: center;
    margin-right: 20px;

`;



const Center = styled.div`
    flex: 1;
    padding: 20px;
    ${mobile({ display: "none" })};
    ${mobileM({ display: "none" })};


`;

const Title = styled.h3`
    margin-bottom: 30px;
`;
const List = styled.ul`
    margin: 0;
    padding: 0;
    list-style: none;
    display: flex;
    flex-wrap: wrap;
`;
const ListItem = styled.li`
    width: 30vh;
    margin-bottom: 10px;

`;


const Right = styled.div`
    flex: 1;
    padding: 20px;
    ${mobile({ backgroundColor: "#FCF5F5" })};
    ${mobileM({ backgroundColor: "#FCF5F5" })};
`;

const ContactItem = styled.div`
    margin-bottom: 20px;
    display: flex;
    align-items: center;

`;

const Payment = styled.img`
    width: 200px;
`;


const Footer = () => {
    return (
        <Container>
            <Left>
                <Logo>Blanvic.</Logo>
                <Desc>
                    Lorem ipsum dolor sit amet consectetur adipisicing elit. Eaque quisquam rem impedit hic laborum eos repudiandae aliquam magnam facilis laudantium tenetur quos architecto aspernatur numquam, eius, velit illum cum omnis?
                </Desc>

                <SocialContainer>
                    <SocialIcon color="3B5999">
                        <Facebook />
                    </SocialIcon>
                    <SocialIcon color="E4405F">
                        <Instagram />
                    </SocialIcon>
                    <SocialIcon color="55ACEE">
                        <Twitter />
                    </SocialIcon>
                    <SocialIcon color="E60023">
                        <Pinterest />
                    </SocialIcon>
                </SocialContainer>
            </Left>

            <Center>
                <Title>Useful Links</Title>
                <List>
                    <ListItem>Home</ListItem>
                    <ListItem>Cart</ListItem>
                    <ListItem>Man Fashion</ListItem>
                    <ListItem>Woman Fashion</ListItem>
                    <ListItem>Accessories</ListItem>
                    <ListItem>My Account</ListItem>
                    <ListItem>Order Tracking</ListItem>
                    <ListItem>Wishlist</ListItem>
                    <ListItem>Wishlist</ListItem>
                    <ListItem>Terms</ListItem>
                </List>
            </Center>
            <Right>
                <Title>Contact</Title>
                <ContactItem><Room style={{marginRight:"10px"}} /> 742 Evergreen Terrace</ContactItem>
                <ContactItem><Phone style={{marginRight:"10px"}} /> +1-403-333-33-33</ContactItem>
                <ContactItem><Mail style={{marginRight:"10px"}} /> contact-us@blanvic.com</ContactItem>
                <Payment src="https://i.ibb.co/Qfvn4z6/payment.png" />
            </Right>
        </Container>

    )
}

export default Footer
